import React, { useEffect, useState } from "react";
import { gapi } from "gapi-script";

const CLIENT_ID = "";
const API_KEY = "";
const SCOPES = "";

const Reserve = () => {
  const [signedIn, setSignedIn] = useState(false);
  const [eventDetails, setEventDetails] = useState({
    summary: "",
    location: "",
    description: "",
    start: "",
    end: "",
  });

  useEffect(() => {
    function initClient() {
      gapi.load("client:auth2", () => {
        gapi.client.init({
          apiKey: API_KEY,
          clientId: CLIENT_ID,
          discoveryDocs: ["https://www.googleapis.com/discovery/v1/apis/calendar/v3/rest"],
          scope: SCOPES,
        });
      });
    }
    initClient();
  }, []);

  const handleSignIn = () => {
    gapi.auth2.getAuthInstance().signIn().then(() => {
      setSignedIn(true);
      alert("Signed in successfully!");
    });
  };

  const handleSignOut = () => {
    gapi.auth2.getAuthInstance().signOut().then(() => {
      setSignedIn(false);
      alert("Signed out successfully!");
    });
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEventDetails({ ...eventDetails, [name]: value });
  };

  const handleCreateEvent = () => {
    const event = {
      summary: eventDetails.summary,
      location: eventDetails.location,
      description: eventDetails.description,
      start: {
        dateTime: eventDetails.start,
        timeZone: "America/Los_Angeles",
      },
      end: {
        dateTime: eventDetails.end,
        timeZone: "America/Los_Angeles",
      },
    };

    gapi.client.calendar.events.insert({
      calendarId: "primary",
      resource: event,
    }).then((response) => {
      alert("Classroom reserved successfully!");
      console.log("Event created: ", response);
    }).catch((error) => {
      console.error("Error creating event: ", error);
    });
  };

  return (
    <div style={{ padding: "2rem" }}>
      <h1>Reserve a Classroom</h1>
      {!signedIn ? (
        <button onClick={handleSignIn} style={{ padding: "0.5rem 1rem", fontSize: "1rem" }}>
          Sign in with Google
        </button>
      ) : (
        <>
          <button onClick={handleSignOut} style={{ padding: "0.5rem 1rem", fontSize: "1rem" }}>
            Sign Out
          </button>
          <div style={{ marginTop: "1rem" }}>
            <h2>Create a Reservation</h2>
            <form
              style={{
                display: "flex",
                flexDirection: "column",
                gap: "1rem",
                maxWidth: "400px",
              }}
            >
              <input
                type="text"
                name="summary"
                placeholder="Class Title"
                value={eventDetails.summary}
                onChange={handleInputChange}
              />
              <input
                type="text"
                name="location"
                placeholder="Classroom Location"
                value={eventDetails.location}
                onChange={handleInputChange}
              />
              <textarea
                name="description"
                placeholder="Description"
                value={eventDetails.description}
                onChange={handleInputChange}
              ></textarea>
              <label>Start Time:</label>
              <input
                type="datetime-local"
                name="start"
                value={eventDetails.start}
                onChange={handleInputChange}
              />
              <label>End Time:</label>
              <input
                type="datetime-local"
                name="end"
                value={eventDetails.end}
                onChange={handleInputChange}
              />
              <button
                type="button"
                onClick={handleCreateEvent}
                style={{ padding: "0.5rem 1rem", fontSize: "1rem" }}
              >
                Reserve Classroom
              </button>
            </form>
          </div>
        </>
      )}
    </div>
  );
};

export default Reserve;
