import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route, Link, Navigate } from "react-router-dom";
import Login from "./components/Login";
import Register from "./components/Register";
import Reserve from "./components/Reserve";

const App = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false); // Authentication state

  const handleLoginSuccess = () => {
    setIsAuthenticated(true); // Update the authentication state
  };

  const handleLogout = () => {
    setIsAuthenticated(false); // Reset the authentication state
  };

  return (
    <Router>
      <div>
        <h1>Welcome to CloudProject</h1>
        <nav>
          {!isAuthenticated ? (
            <>
              <Link to="/login">Login</Link>
              <br />
              <Link to="/register">Register</Link>
            </>
          ) : (
            <>
              <Link to="/reserve">Reserve Classroom</Link>
              <br />
              <button onClick={handleLogout}>Logout</button>
            </>
          )}
        </nav>

        <Routes>
          {/* Public Routes */}
          <Route path="/login" element={<Login onLoginSuccess={handleLoginSuccess} />} />
          <Route path="/register" element={<Register />} />

          {/* Protected Route */}
          <Route
            path="/reserve"
            element={
              isAuthenticated ? <Reserve /> : <Navigate to="/login" replace />
            }
          />

          {/* Default Route */}
          <Route
            path="/"
            element={
              isAuthenticated ? <Navigate to="/reserve" replace /> : <Navigate to="/login" replace />
            }
          />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
