import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate, Link } from "react-router-dom";
import Login from "./components/Login";
import Register from "./components/Register";
import Reserve from "./components/Reserve";

const App = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [userEmail, setUserEmail] = useState(null);

  const handleLoginSuccess = (email) => {
    setIsAuthenticated(true);
    setUserEmail(email); // Store the authenticated user's email
  };

  return (
    <Router>
      <div>
        <h1>Welcome to Cloud Project</h1>
        <nav>
          {!isAuthenticated ? (
            <Link to="/login">Login</Link>
          ) : (
            <>
              <Link to="/reserve">Reserve</Link>
              <br />
              <Link to="/register">Register</Link>
            </>
          )}
        </nav>
        <Routes>
          <Route path="/login" element={<Login onLoginSuccess={handleLoginSuccess} />} />
          {isAuthenticated && (
            <Route path="/reserve" element={<Reserve userEmail={userEmail} />} />
          )}
          {isAuthenticated && <Route path="/register" element={<Register />} />}
          <Route
            path="/"
            element={
              isAuthenticated ? <Navigate to="/reserve" replace /> : <Navigate to="/login" replace />
            }
          />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
